        </button>
        <button 
          className={answers.yearsInItaly === '1to2' ? 'selected' : ''}
          onClick={() => handleAnswer('yearsInItaly', '1to2')}
        >
          1-2 anni
        </button>
        <button 
          className={answers.yearsInItaly === 'more2' ? 'selected' : ''}
          onClick={() => handleAnswer('yearsInItaly', 'more2')}
        >
