    const {
      isNAI,
      motherLanguage,
      previousAnswers
    } = userProfile;
    switch (sectionId) {
      case 'reading':
        // Seleziona domande di lettura adattive
        return MockQuestionService.selectAdaptiveQuestions(mockQuestionBank.reading, previousAnswers, 3 // numero domande
        );
      case 'grammar':
        return MockQuestionService.selectAdaptiveQuestions(mockQuestionBank.grammar, previousAnswers, 4);
      case 'writing':
        return MockQuestionService.selectAdaptiveQuestions(mockQuestionBank.writing, previousAnswers, 2);
      case 'speaking':
        return MockQuestionService.selectAdaptiveQuestions(mockQuestionBank.speaking, previousAnswers, 2);
      default:
        return [];
    }
  },
  // Selezione adattiva domande
  selectAdaptiveQuestions: (questionPool, previousAnswers, count) => {
    // Algoritmo semplificato di selezione adattiva
    const performanceScore = MockQuestionService.calculatePerformance(previousAnswers);
    let targetLevel;
    if (performanceScore > 0.8) targetLevel = ['B2', 'C1', 'C2'];else if (performanceScore > 0.6) targetLevel = ['B1', 'B2'];else if (performanceScore > 0.4) targetLevel = ['A2', 'B1'];else targetLevel = ['A1', 'A2'];

    // Filtra domande per livello target
    const filteredQuestions = questionPool.filter(q => !q.level || targetLevel.includes(q.level));

    // Seleziona random dal pool filtrato
    const shuffled = filteredQuestions.sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
  },
  // Calcola performance dalle risposte precedenti
  calculatePerformance: answers => {
    const answerKeys = Object.keys(answers);
    if (answerKeys.length === 0) return 0.5; // Punteggio neutro

    let correctCount = 0;
    let totalCount = 0;
    answerKeys.forEach(key => {
      const answer = answers[key];
      if (typeof answer === 'object' && answer.isCorrect !== undefined) {
        totalCount++;
        if (answer.isCorrect) correctCount++;
      } else if (answer === 'correct') {
        totalCount++;
        correctCount++;
      } else if (['wrong1', 'wrong2', 'wrong3'].includes(answer)) {
        totalCount++;
      }
    });
    return totalCount > 0 ? correctCount / totalCount : 0.5;
  },
