// Simulazione AI Service
export const MockQuestionService = {
  // Genera banco domande iniziale
  generateTestBank: async () => {
    await new Promise(resolve => setTimeout(resolve, 1000)); // Simula delay API
    return mockQuestionBank;
  },

  // Genera domande per sezione specifica
  generateSectionQuestions: async (sectionId, userProfile) => {
    await new Promise(resolve => setTimeout(resolve, 800)); // Simula delay
    
    const { isNAI, motherLanguage, previousAnswers } = userProfile;
    
    switch(sectionId) {
      case 'reading':
        // Seleziona domande di lettura adattive
        return MockQuestionService.selectAdaptiveQuestions(
          mockQuestionBank.reading, 
          previousAnswers, 
          3 // numero domande
        );
        
      case 'grammar':
        return MockQuestionService.selectAdaptiveQuestions(
          mockQuestionBank.grammar, 
          previousAnswers, 
          4
        );
        
      case 'writing':
        return MockQuestionService.selectAdaptiveQuestions(
          mockQuestionBank.writing, 
          previousAnswers, 
          2
        );
        
      case 'speaking':
        return MockQuestionService.selectAdaptiveQuestions(
          mockQuestionBank.speaking, 
          previousAnswers, 
          2
        );
        
      default:
        return [];
    }
  },

  // Selezione adattiva domande
  selectAdaptiveQuestions: (questionPool, previousAnswers, count) => {
    // Algoritmo semplificato di selezione adattiva
    const performanceScore = MockQuestionService.calculatePerformance(previousAnswers);
    
    let targetLevel;
    if (performanceScore > 0.8) targetLevel = ['B2', 'C1', 'C2'];
    else if (performanceScore > 0.6) targetLevel = ['B1', 'B2'];
    else if (performanceScore > 0.4) targetLevel = ['A2', 'B1'];
    else targetLevel = ['A1', 'A2'];

    // Filtra domande per livello target
    const filteredQuestions = questionPool.filter(q => 
      !q.level || targetLevel.includes(q.level)
    );

    // Seleziona random dal pool filtrato
    const shuffled = filteredQuestions.sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
  },

  // Calcola performance dalle risposte precedenti
  calculatePerformance: (answers) => {
    const answerKeys = Object.keys(answers);
    if (answerKeys.length === 0) return 0.5; // Punteggio neutro

    let correctCount = 0;
    let totalCount = 0;

    answerKeys.forEach(key => {
      const answer = answers[key];
      if (typeof answer === 'object' && answer.isCorrect !== undefined) {
        totalCount++;
        if (answer.isCorrect) correctCount++;
      } else if (answer === 'correct') {
        totalCount++;
        correctCount++;
      } else if (['wrong1', 'wrong2', 'wrong3'].includes(answer)) {
        totalCount++;
      }
    });

    return totalCount > 0 ? correctCount / totalCount : 0.5;
  },

  // Valuta risposta scritta (simulata)
  evaluateWriting: async (text, level) => {
    await new Promise(resolve => setTimeout(resolve, 1500));
    
    const wordCount = text.trim().split(/\s+/).length;
    const hasGoodLength = wordCount >= 20;
    const hasVariety = new Set(text.toLowerCase().split(/\s+/)).size / wordCount > 0.6;
    
    let score = 0.5;
    if (hasGoodLength) score += 0.2;
    if (hasVariety) score += 0.2;
    if (text.includes('.') && text.includes(',')) score += 0.1;
    
    return {
      score: Math.min(score, 1.0),
      feedback: score > 0.7 ? 'Ottima risposta!' : 
                score > 0.5 ? 'Buona risposta, ma puoi migliorare.' : 
                'Prova a scrivere di più e con più varietà.'
    };
  },

  // Valuta registrazione audio (simulata)
  evaluateSpeaking: async (audioData, level) => {
    await new Promise(resolve => setTimeout(resolve, 2000));
    
    const duration = audioData.duration || 5000;
    const hasGoodDuration = duration > 10000 && duration < 120000;
    
    let score = 0.6; // Punteggio base
    if (hasGoodDuration) score += 0.3;
    
    return {
      score: Math.min(score, 1.0),
      feedback: score > 0.8 ? 'Eccellente pronuncia e fluidità!' :
                score > 0.6 ? 'Buona pronuncia, continua così!' :
                'Continua a praticare la pronuncia.'
    };
  }
};

// Algoritmo di scoring QCER migliorato
export const QCERScoring = {
  calculateLevel: (allAnswers) => {
    const sectionScores = {
      reading: QCERScoring.evaluateSection(allAnswers, 'reading'),
      grammar: QCERScoring.evaluateSection(allAnswers, 'grammar'),
      writing: QCERScoring.evaluateSection(allAnswers, 'writing'),
      speaking: QCERScoring.evaluateSection(allAnswers, 'speaking')
    };

    // Media ponderata
    const totalScore = (
      sectionScores.reading * 0.25 +
      sectionScores.grammar * 0.35 +
      sectionScores.writing * 0.2 +
      sectionScores.speaking * 0.2
    );

    // Mapping preciso score → QCER
    if (totalScore >= 0.92) return 'C2';
    if (totalScore >= 0.82) return 'C1';
    if (totalScore >= 0.68) return 'B2';
    if (totalScore >= 0.52) return 'B1';
    if (totalScore >= 0.32) return 'A2';
    return 'A1';
  },

  evaluateSection: (answers, sectionPrefix) => {
    const sectionAnswers = Object.keys(answers)
      .filter(key => key.includes(sectionPrefix))
      .map(key => answers[key]);

    if (sectionAnswers.length === 0) return 0.5;

    let totalScore = 0;
    let count = 0;

    sectionAnswers.forEach(answer => {
      count++;
      if (typeof answer === 'object' && answer.score !== undefined) {
        totalScore += answer.score;
      } else if (answer === 'correct') {
        totalScore += 1.0;
      } else if (['wrong1', 'wrong2', 'wrong3'].includes(answer)) {
        totalScore += 0.0;
      } else {
        totalScore += 0.5; // Risposta parziale
      }
    });

    return count > 0 ? totalScore / count : 0.5;
  }
};
