    words.forEach(word => {
      orthographicErrors.forEach(errorPattern => {
        if (errorPattern.test(word)) errors++;
      });
    });
    const errorRate = errors / Math.max(words.length, 1);
    return Math.max(0, 1 - errorRate * 3);
  },
  // 2. ANALISI MORFOLOGICA
