        console.log(`ðŸ“Š Sezione ${section}:`, sectionAnswers.length, 'risposte');
        
        if (sectionAnswers.length > 0) {
          const correctAnswers = sectionAnswers.filter(answer => {
            if (typeof answer === 'object' && answer.isCorrect) return true;
