            if (typeof answer === 'object' && answer.score && answer.score > 0.6) return true;
            if (typeof answer === 'string' && answer.length > 10) return true; // Testi scritti
            return false;
          }).length;
          
          sectionScores[section] = (correctAnswers / sectionAnswers.length) * 100;
        } else {
