};

// COMPONENTE SPEAKING RECORDER MIGLIORATO
const SpeakingRecorder = ({ questionId, onRecordingComplete, maxDuration }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [audioURL, setAudioURL] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [hasError, setHasError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [recordingTime, setRecordingTime] = useState(0);

  useEffect(() => {
    let interval;
    if (isRecording) {
      interval = setInterval(() => {
        setRecordingTime(time => time + 1);
      }, 1000);
    } else {
      setRecordingTime(0);
    }
    return () => clearInterval(interval);
  }, [isRecording]);

  const startRecording = async () => {
    try {
      // Controlla se il browser supporta getUserMedia
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('Il tuo browser non supporta la registrazione audio');
      }

      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (event) => chunks.push(event.data);
      recorder.onstop = () => {
        const audioBlob = new Blob(chunks, { type: 'audio/wav' });
        const audioUrl = URL.createObjectURL(audioBlob);
        setAudioURL(audioUrl);
        onRecordingComplete({
          audioBlob,
          audioUrl,
          duration: recordingTime * 1000
        });
        
        // Stoppa lo stream per liberare il microfono
        stream.getTracks().forEach(track => track.stop());
      };

      recorder.start();
      setMediaRecorder(recorder);
      setIsRecording(true);
      setHasError(false);
      setRecordingTime(0);

      // Auto-stop dopo maxDuration
      setTimeout(() => {
        if (recorder.state === 'recording') {
          stopRecording();
        }
      }, maxDuration * 1000);

    } catch (error) {
      console.error('Errore accesso microfono:', error);
      setHasError(true);
      
      if (error.name === 'NotAllowedError') {
        setErrorMessage('Accesso al microfono negato. Abilita il microfono nelle impostazioni del browser.');
      } else if (error.name === 'NotFoundError') {
        setErrorMessage('Nessun microfono trovato. Collega un microfono e riprova.');
      } else {
        setErrorMessage('Errore microfono: ' + error.message);
      }
    }
  };

  const stopRecording = () => {
    if (mediaRecorder && mediaRecorder.state === 'recording') {
      mediaRecorder.stop();
      setIsRecording(false);
    }
  };

  const skipSpeaking = () => {
    // Fornisce un punteggio neutro per chi salta la sezione parlato
    console.log('ðŸ”‡ Sezione parlato saltata per:', questionId);
    onRecordingComplete({
      audioBlob: null,
      audioUrl: null,
      duration: 0,
      score: 0.5,
      skipped: true
    });
  };

  return (
    <div className="speaking-recorder">
      {hasError ? (
        <div className="recording-error">
          <p className="error-message">âš ï¸ {errorMessage}</p>
          <div className="error-actions">
            <button 
              className="retry-button"
              onClick={() => {
                setHasError(false);
                setErrorMessage('');
              }}
            >
              ðŸ”„ Riprova
            </button>
            <button 
              className="skip-button"
              onClick={skipSpeaking}
            >
              â­ï¸ Salta Sezione Parlato
            </button>
          </div>
          <p className="skip-note">
            Nota: Saltando la sezione parlato, il test sarÃ  meno preciso ma potrai comunque ottenere una valutazione.
          </p>
        </div>
      ) : (
        <>
          <div className="recording-info">
            <p>ðŸ“‹ Hai <strong>{maxDuration} secondi</strong> per rispondere.</p>
            <p>ðŸ’¡ Parla chiaramente verso il microfono</p>
          </div>

          <div className="recording-controls">
            {!isRecording && !audioURL ? (
              <div className="recording-start">
                <button 
                  className="record-button"
                  onClick={startRecording}
                >
                  ðŸŽ¤ Inizia Registrazione
                </button>
                
                <button 
                  className="skip-speaking-button"
                  onClick={skipSpeaking}
                  style={{ 
                    marginTop: '15px', 
                    backgroundColor: '#6c757d', 
                    color: 'white',
                    border: 'none',
                    padding: '8px 16px',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  â­ï¸ Salta questa sezione
                </button>
              </div>
            ) : isRecording ? (
              <div className="recording-active">
                <div className="recording-indicator">
                  <div className="pulse-dot" style={{
                    width: '10px',
                    height: '10px',
                    backgroundColor: '#ff0000',
                    borderRadius: '50%',
                    display: 'inline-block',
                    marginRight: '8px',
                    animation: 'pulse 1s infinite'
                  }}></div>
                  <span>ðŸ”´ Registrazione in corso...</span>
                </div>
                
                <div className="timer">
                  Tempo: {recordingTime}s / {maxDuration}s
                </div>

                <button 
                  className="stop-button"
                  onClick={stopRecording}
                  style={{
                    backgroundColor: '#dc3545',
                    color: 'white',
                    border: 'none',
                    padding: '10px 20px',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginTop: '10px'
                  }}
                >
                  â¹ï¸ Ferma Registrazione
                </button>
              </div>
            ) : audioURL ? (
              <div className="recording-complete">
                <p>âœ… Registrazione completata!</p>
                
                <div className="audio-controls">
                  <audio controls src={audioURL} style={{ width: '100%', marginBottom: '10px' }} />
                </div>

                <div className="action-buttons">
                  <button 
                    className="re-record-button"
                    onClick={() => {
                      setAudioURL(null);
                      setIsRecording(false);
                      setRecordingTime(0);
                    }}
                    style={{
                      backgroundColor: '#ffc107',
                      color: '#212529',
                      border: 'none',
                      padding: '8px 16px',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      marginRight: '10px'
                    }}
                  >
                    ðŸ”„ Registra di Nuovo
                  </button>
                  
                  <button 
                    className="skip-button"
                    onClick={skipSpeaking}
                    style={{
                      backgroundColor: '#6c757d',
                      color: 'white',
                      border: 'none',
                      padding: '8px 16px',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    â­ï¸ Salta comunque
                  </button>
                </div>
              </div>
            ) : null}
          </div>
          
          {!audioURL && !isRecording && (
            <div className="speaking-help">
              <p style={{ fontSize: '14px', color: '#6c757d', marginTop: '15px' }}>
                âš ï¸ Se non hai microfono disponibile, puoi saltare questa sezione
              </p>
            </div>
          )}
        </>
      )}
      
      <style jsx>{`
        @keyframes pulse {
          0% { opacity: 1; }
          50% { opacity: 0.5; }
          100% { opacity: 1; }
        }
        
        .speaking-recorder {
          border: 1px solid #ddd;
          border-radius: 8px;
          padding: 20px;
          margin: 10px 0;
          background-color: #f9f9f9;
        }
        
        .recording-info {
          margin-bottom: 15px;
          padding: 10px;
          background-color: #e3f2fd;
          border-radius: 4px;
        }
        
        .recording-error {
          padding: 15px;
          background-color: #ffebee;
          border: 1px solid #f44336;
          border-radius: 4px;
        }
        
        .error-message {
          color: #d32f2f;
          font-weight: bold;
          margin-bottom: 10px;
        }
        
        .record-button {
          background-color: #28a745;
          color: white;
          border: none;
          padding: 12px 24px;
          border-radius: 6px;
          font-size: 16px;
          cursor: pointer;
          transition: background-color 0.2s;
        }
        
        .record-button:hover {
          background-color: #218838;
        }
        
        .recording-active {
          text-align: center;
          padding: 20px;
        }
        
        .recording-indicator {
          font-size: 18px;
          margin-bottom: 10px;
        }
        
        .timer {
          font-size: 16px;
          margin-bottom: 15px;
          color: #dc3545;
          font-weight: bold;
        }
        
        .recording-complete {
          text-align: center;
        }
        
        .action-buttons {
          margin-top: 15px;
        }
      `}</style>
    </div>
  );
};

export default TestIngresso;
