// EnhancedSupportoLinguistico.js - Sistema integrato con layer superiore
import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../../../contexts/contexts/UserContext';
import { translationSystem } from './TranslationSystem';
import SupportoLinguistico from './SupportoLinguistico';
import './TranslationSystem.css';
import './SupportoLinguistico.css';

const EnhancedSupportoLinguistico = ({ currentSection, isTestMode = false }) => {
  const { user } = useContext(UserContext);
  const [isWidgetVisible, setIsWidgetVisible] = useState(false);
  const [translationStats, setTranslationStats] = useState({});
  const [mostUsedWords, setMostUsedWords] = useState([]);
  const [personalDictionary, setPersonalDictionary] = useState({});

  // Inizializza il sistema di traduzione
  useEffect(() => {
    if (!isTestMode && user) {
      translationSystem.initialize({ user });
      
      // Carica statistiche
      loadTranslationData();
      
      // Mostra prompt smart disable dopo 2 minuti in sezioni di esercizio
      if (currentSection && ['lettura-assistita', 'esercizi', 'scrittura'].includes(currentSection)) {
        const timer = setTimeout(() => {
          translationSystem.showSmartDisablePrompt();
        }, 120000); // 2 minuti
        
        return () => clearTimeout(timer);
      }
    }
    
    // Cleanup quando il componente viene dismesso
    return () => {
      if (translationSystem) {
        translationSystem.removeEventListeners();
      }
    };
  }, [user, isTestMode, currentSection]);

  const loadTranslationData = () => {
    setTranslationStats(translationSystem.getUsageStats());
    setMostUsedWords(translationSystem.getMostTranslatedWords(10));
    setPersonalDictionary(translationSystem.getPersonalDictionary());
  };

  const toggleWidget = () => {
    setIsWidgetVisible(!isWidgetVisible);
    if (!isWidgetVisible) {
      // Ricarica dati quando apri il widget
      loadTranslationData();
    }
  };

  const closeWidget = () => {
    setIsWidgetVisible(false);
  };

  // Render del widget avanzato con statistiche
  const renderAdvancedWidget = () => {
    if (!isWidgetVisible) return null;

    return (
      <div className="enhanced-supporto-widget">
        {/* Widget originale */}
        <SupportoLinguistico 
          onClose={closeWidget} 
          currentSection={currentSection} 
        />
        
        {/* Pannello statistiche aggiuntivo */}
        <div className="translation-stats-panel">
          <h4>üìä Le tue statistiche</h4>
          
          {/* Parole pi√π tradotte */}
          {mostUsedWords.length > 0 && (
            <div className="most-used-section">
              <h5>üî• Parole pi√π cercate</h5>
              <div className="words-list">
                {mostUsedWords.slice(0, 5).map(({ word, count }) => (
                  <div key={word} className="word-stat">
                    <span className="word">{word}</span>
                    <span className="count">{count}x</span>
                  </div>
                ))}
              </div>
            </div>
          )}
          
          {/* Dizionario personale */}
          {Object.keys(personalDictionary).length > 0 && (
            <div className="personal-dict-section">
              <h5>üíæ Dizionario Personale ({Object.keys(personalDictionary).length} parole)</h5>
              <div className="dict-preview">
                {Object.entries(personalDictionary).slice(0, 3).map(([word, data]) => (
                  <div key={word} className="dict-item">
                    <span className="word">{word}</span>
                    <span className="translation">{data.translation}</span>
                  </div>
                ))}
                {Object.keys(personalDictionary).length > 3 && (
                  <div className="more-items">+{Object.keys(personalDictionary).length - 3} altre...</div>
                )}
              </div>
            </div>
          )}
          
          {/* Controlli avanzati */}
          <div className="advanced-controls">
            <button 
              className="control-btn"
              onClick={() => translationSystem.showSmartDisablePrompt()}
            >
              üéØ Modalit√† Test
            </button>
            
            <button 
              className="control-btn"
              onClick={() => exportPersonalDictionary()}
            >
              üì§ Esporta Dizionario
            </button>
            
            <button 
              className="control-btn danger"
              onClick={() => clearAllData()}
            >
              üóëÔ∏è Cancella Dati
            </button>
          </div>
        </div>
      </div>
    );
  };

  // Funzioni utility
  const exportPersonalDictionary = () => {
    const dataStr = JSON.stringify(personalDictionary, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    
    const link = document.createElement('a');
    link.href = url;
    link.download = 'dizionario-personale.json';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const clearAllData = () => {
    if (confirm('Sei sicuro di voler cancellare tutti i dati del supporto linguistico?')) {
      localStorage.removeItem('translation_personal_dictionary');
      localStorage.removeItem('translation_usage_stats');
      setPersonalDictionary({});
      setTranslationStats({});
      setMostUsedWords([]);
      alert('Dati cancellati con successo!');
    }
  };

  // Render del pulsante trigger migliorato
  const renderTriggerButton = () => {
    if (isTestMode) return null;

    const hasStats = mostUsedWords.length > 0 || Object.keys(personalDictionary).length > 0;

    return (
      <button 
        className={`enhanced-trigger-button ${hasStats ? 'has-stats' : ''}`}
        onClick={toggleWidget}
        title="Supporto Linguistico Avanzato"
      >
        <span className="trigger-icon">üåç</span>
        <span className="trigger-text">Supporto</span>
        {hasStats && (
          <span className="stats-badge">
            {Object.keys(personalDictionary).length}
          </span>
        )}
      </button>
    );
  };

  // Render indicatore stato traduzione
  const renderTranslationStatus = () => {
    if (isTestMode) return null;

    const isHoverActive = translationSystem.isActive && !translationSystem.isSmartDisabled;

    return (
      <div className={`translation-status ${isHoverActive ? 'active' : 'inactive'}`}>
        <div className="status-indicator">
          {isHoverActive ? (
            <>
              <span className="status-dot active"></span>
              <span className="status-text">Traduzione attiva</span>
            </>
          ) : (
            <>
              <span className="status-dot inactive"></span>
              <span className="status-text">Traduzione disattiva</span>
            </>
          )}
        </div>
        
        {isHoverActive && (
          <div className="usage-hint">
            üí° Passa il mouse su una parola o seleziona del testo per tradurre
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="enhanced-supporto-container">
      {/* Sistema di traduzione hover/selection (invisibile ma attivo) */}
      {/* Il TranslationSystem lavora automaticamente in background */}
      
      {/* Indicatore stato */}
      {renderTranslationStatus()}
      
      {/* Widget avanzato */}
      {renderAdvancedWidget()}
      
      {/* Pulsante trigger */}
      {renderTriggerButton()}
      
      {/* Shortcut keyboard per power users */}
      <KeyboardShortcuts onToggleWidget={toggleWidget} />
    </div>
  );
};

// Componente per gestire shortcut da tastiera
const KeyboardShortcuts = ({ onToggleWidget }) => {
  useEffect(() => {
    const handleKeyDown = (event) => {
      // Ctrl/Cmd + Shift + T = Toggle widget
      if ((event.ctrlKey || event.metaKey) && event.shiftKey && event.key === 'T') {
        event.preventDefault();
        onToggleWidget();
      }
      
      // Ctrl/Cmd + Shift + D = Toggle smart disable
      if ((event.ctrlKey || event.metaKey) && event.shiftKey && event.key === 'D') {
        event.preventDefault();
        translationSystem.isSmartDisabled 
          ? translationSystem.disableSmartDisable()
          : translationSystem.enableSmartDisable();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [onToggleWidget]);

  return null;
};

// Hook personalizzato per usare il sistema in altri componenti
export const useTranslationSystem = () => {
  const [isActive, setIsActive] = useState(false);
  const [personalDictionary, setPersonalDictionary] = useState({});
  const [usageStats, setUsageStats] = useState({});

  useEffect(() => {
    const checkStatus = () => {
      setIsActive(translationSystem.isActive && !translationSystem.isSmartDisabled);
      setPersonalDictionary(translationSystem.getPersonalDictionary());
      setUsageStats(translationSystem.getUsageStats());
    };

    checkStatus();
    
    // Ricontrolla ogni 2 secondi
    const interval = setInterval(checkStatus, 2000);
    return () => clearInterval(interval);
  }, []);

  return {
    isActive,
    personalDictionary,
    usageStats,
    mostUsedWords: translationSystem.getMostTranslatedWords(10),
    enableSmartDisable: () => translationSystem.enableSmartDisable(),
    disableSmartDisable: () => translationSystem.disableSmartDisable(),
    playAudio: (text, lang) => translationSystem.playAudio(text, lang)
  };
};

export default EnhancedSupportoLinguistico;
