import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { UserProfileProvider } from './hooks/useUserProfile';

// Importa i componenti principali
import OnboardingTest from './components/features/onboarding/OnboardingTest';
import Dashboard from './components/features/dashboard/Dashboard';
import LetturaAssistita from './components/features/lettura/LetturaAssistita';
import SezioneScrittura from './components/features/scrittura/SezioneScrittura';
import Layout from './components/layout/Layout';

/**
 * Componente principale dell'applicazione
 */
function App() {
  return (
    <UserProfileProvider>
      <Router>
        <Routes>
          {/* Route pubblica per il test di livello iniziale */}
          <Route path="/onboarding" element={<OnboardingTest />} />

          {/* Route protette all'interno del layout principale */}
          <Route path="/" element={<Layout />}>
            <Route index element={<Navigate to="/dashboard" replace />} />
            <Route path="dashboard" element={<Dashboard />} />
            <Route path="lettura/:id" element={<LetturaAssistita />} />
            <Route path="scrittura" element={<SezioneScrittura />} />
          </Route>

          {/* Route di fallback */}
          <Route path="*" element={<Navigate to="/dashboard" replace />} />
        </Routes>
      </Router>
    </UserProfileProvider>
  );
}

export default App;
