// Algoritmo di scoring QCER con debug dettagliato
export const QCERScoring = {
  calculateLevel: (scores) => {
    console.log(`ðŸŽ¯ === RISULTATI DETTAGLIATI TEST QCER ===`);
    console.log(`ðŸ“– Lettura/Comprensione: ${scores.reading.toFixed(1)}%`);
    console.log(`ðŸ“ Grammatica: ${scores.grammar.toFixed(1)}%`);
    console.log(`âœï¸ Scrittura: ${scores.writing.toFixed(1)}%`);
    console.log(`ðŸ—£ï¸ Parlato: ${scores.speaking.toFixed(1)}%`);

    const weights = {
      reading: 0.25,
      grammar: 0.25, 
      writing: 0.3,
      speaking: 0.2
    };

    const totalScore = 
      scores.reading * weights.reading +
      scores.grammar * weights.grammar +
      scores.writing * weights.writing +
      scores.speaking * weights.speaking;

    console.log(`ðŸŽ¯ Punteggio Totale: ${totalScore.toFixed(1)}%`);

    const levels = {
      'A1': { min: 0, max: 32, belt: 'yellow' },
      'A2': { min: 32, max: 52, belt: 'orange' }, 
      'B1': { min: 52, max: 68, belt: 'green' },
      'B2': { min: 68, max: 82, belt: 'blue' },
      'C1': { min: 82, max: 92, belt: 'brown' },
      'C2': { min: 92, max: 100, belt: 'black' }
    };

    let assignedLevel = 'A1';
    for (const [level, range] of Object.entries(levels)) {
      if (totalScore >= range.min && totalScore < range.max) {
        assignedLevel = level;
        break;
      }
    }

    console.log(`ðŸ¥‹ Livello Assegnato: ${assignedLevel}`);
    console.log(`ðŸ” Mapping: A1(0-32%), A2(32-52%), B1(52-68%), B2(68-82%), C1(82-92%), C2(92%+)`);

    return {
      level: assignedLevel,
      score: totalScore,
      belt: levels[assignedLevel].belt,
      breakdown: scores
    };
  }
};
