// TranslationSystem/index.js - Export principale per il sistema di traduzione avanzato

// Import componenti principali
import TranslationSystem, { translationSystem } from './TranslationSystem';
import EnhancedSupportoLinguistico, { useTranslationSystem } from './EnhancedSupportoLinguistico';

// Import CSS (per bundler che supportano CSS imports)
import './TranslationSystem.css';
import './EnhancedSupporto.css';

// Export default: componente completo pronto all'uso
export default EnhancedSupportoLinguistico;

// Export componenti individuali
export {
  // Classe sistema di traduzione
  TranslationSystem,
  
  // Istanza singleton globale
  translationSystem,
  
  // Componente wrapper completo
  EnhancedSupportoLinguistico,
  
  // Hook personalizzato per React
  useTranslationSystem
};

// Export per backward compatibility e flessibilità
export const TranslationSystemComponents = {
  Enhanced: EnhancedSupportoLinguistico,
  System: TranslationSystem,
  instance: translationSystem,
  hook: useTranslationSystem
};

// Utility per inizializzazione rapida
export const initializeTranslationSystem = (userContext, options = {}) => {
  const {
    enableSmartDisable = true,
    autoShowPrompt = true,
    promptDelay = 120000, // 2 minuti
    ...otherOptions
  } = options;

  // Inizializza il sistema
  translationSystem.initialize(userContext);
  
  // Configura opzioni avanzate se specificate
  if (enableSmartDisable && autoShowPrompt) {
    setTimeout(() => {
      translationSystem.showSmartDisablePrompt();
    }, promptDelay);
  }
  
  console.log('🌍 TranslationSystem initialized with options:', options);
  
  return translationSystem;
};

// Helper per verificare se il sistema è attivo
export const isTranslationActive = () => {
  return translationSystem && translationSystem.isActive && !translationSystem.isSmartDisabled;
};

// Helper per statistics rapide
export const getTranslationStats = () => {
  if (!translationSystem) return null;
  
  return {
    personalDictionary: translationSystem.getPersonalDictionary(),
    usageStats: translationSystem.getUsageStats(),
    mostUsedWords: translationSystem.getMostTranslatedWords(10),
    isActive: isTranslationActive()
  };
};

// Export configurazioni predefinite
export const TRANSLATION_CONFIGS = {
  // Configurazione per studenti principianti
  BEGINNER: {
    enableSmartDisable: true,
    autoShowPrompt: true,
    promptDelay: 60000, // 1 minuto
    hoverDelay: 300
  },
  
  // Configurazione per studenti intermedi
  INTERMEDIATE: {
    enableSmartDisable: true,
    autoShowPrompt: true,
    promptDelay: 180000, // 3 minuti
    hoverDelay: 500
  },
  
  // Configurazione per studenti avanzati
  ADVANCED: {
    enableSmartDisable: true,
    autoShowPrompt: false,
    promptDelay: 300000, // 5 minuti
    hoverDelay: 800
  },
  
  // Configurazione per testing/demo
  DEMO: {
    enableSmartDisable: false,
    autoShowPrompt: false,
    promptDelay: 0,
    hoverDelay: 200
  }
};

// Versione e info del sistema
export const TRANSLATION_SYSTEM_INFO = {
  version: '1.0.0',
  author: 'NAI-Hub Team',
  description: 'Sistema di traduzione avanzato con hover, selection e analytics',
  features: [
    'Hover translation',
    'Selection translation', 
    'Smart activation',
    'Personal dictionary',
    'Usage analytics',
    'Audio pronunciation',
    'Smart disable mode',
    'Mobile responsive',
    'Dark mode support'
  ]
};
